package com.company.bazar.model;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;import org.springframework.context.annotation.EnableMBeanExport;import java.util.List;@Getter@Setter@Entitypublic class Client {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    private Long idClient ;    private String nameClient ;    private  String lastnameClient;    private String dni;    @OneToMany (mappedBy = "client")    @JsonIgnore    private List<Sale> sales ;    public Client() {    }    public Client(Long idClient, String nameClient, String lastnameClient, String dni, List<Sale> sales) {        this.idClient = idClient;        this.nameClient = nameClient;        this.lastnameClient = lastnameClient;        this.dni = dni;        this.sales = sales;    }}