package com.company.bazar.model;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;import java.util.List;@Getter @Setter@Entitypublic class Product {    @Id    @GeneratedValue(strategy =  GenerationType.SEQUENCE)    private Long codProduct;    private String nameProduct;    private double cost ;    private Long stock;    @OneToMany (mappedBy = "product")    @JsonIgnore    private List<SaleProduct> saleList;    public Product() {    }    public Product(Long codProduct, String nameProduct, double cost, Long stock, List<SaleProduct> saleList) {        this.codProduct = codProduct;        this.nameProduct = nameProduct;        this.cost = cost;        this.stock = stock;        this.saleList = saleList;    }}