package com.company.bazar.model;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.time.LocalDate;import java.util.List;@Getter@Setter@NoArgsConstructor @AllArgsConstructor@Entitypublic class Sale {    @Id    @GeneratedValue(strategy =  GenerationType.SEQUENCE)    @Column(name =  "cod_sale")    private Long codSale;    @Temporal(TemporalType.DATE)    private LocalDate saleDate;    @ManyToOne    @JoinColumn(name="idClient", nullable = false)    private Client client;    @OneToMany(mappedBy = "sale", cascade = CascadeType.ALL)    private List<SaleProduct> products  ;    private double totalSale;}