package com.company.bazar.repository;import com.company.bazar.model.Sale;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.time.LocalDate;import java.util.Date;import java.util.List;@Repositorypublic interface ISaleRepository extends JpaRepository<Sale, Long> {    public List<Sale> findBySaleDate(LocalDate date);    @Query("SELECT s FROM Sale s WHERE s.totalSale = (SELECT MAX(s2.totalSale) FROM Sale s2)")    Sale findSaleWithMaxTotal();}